import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import { Container, ConteudoForm, ConteudoTitulo, Titulo, BotaoAcao, ButtonInfo, AlertSuccess, AlertDanger, Form, Label, Input, ButtonSuccess } from './styles';

export const Cadastrar = () => {

  const [produto, setProduto] = useState({
    nome: '',
    descricao: '',
    tensao: '',
    marca: ''
  });

  const [status, setStatus] = useState({
    type: '',
    mensagem: ''
  })

  const valorInput = e => setProduto({ ...produto, [e.target.name]: e.target.value });

  const cadProduto = async e => {
    e.preventDefault();
    //console.log(produto.nome);

    await fetch("https://www.armandosoares.com.br/grupoplan/api/produto", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(produto)
    })
      .then((response) => response.json())
      .then((responseJson) => {
        //console.log(responseJson)
        if (responseJson.erro) {
          setStatus({
            type: 'erro',
            mensagem: responseJson.status
          });
        } else {
          setStatus({
            type: 'success',
            mensagem: responseJson.status
          });
        }
      }).catch(() => {
        setStatus({
          type: 'erro',
          mensagem: 'Produto n√£o cadastro com sucesso, tente mais tarde!'
        });
      });
  }

  return (
    <Container>
      <ConteudoForm>
        <ConteudoTitulo>
          <Titulo>Cadastrar</Titulo>
          <BotaoAcao>
          <Link to="/">
            <button class="button button--nanuk button--round-l button--text-thick button--inverted">
            üìì <span>L</span><span>I</span><span>S</span><span>T</span><span>A</span><span>R</span>
            </button>
          </Link>
          </BotaoAcao>
        </ConteudoTitulo>

        {status.type === 'erro' ? <AlertDanger>{status.mensagem}</AlertDanger> : ""}
        {status.type === 'success' ? <AlertSuccess>{status.mensagem}</AlertSuccess> : ""}

        <Form onSubmit={cadProduto}>
          <Label>Nome: </Label>
          <Input type="text" name="nome" placeholder="Nome do produto" onChange={valorInput} />

          <Label>Descri√ß√£o: </Label>
          <Input type="text" name="descricao" placeholder="Descri√ß√£o do produto" onChange={valorInput} />

          <Label>Tens√£o: </Label>
          <Input type="text" name="tensao" placeholder="Tens√£o do produto" onChange={valorInput} />

          <Label>Marca: </Label>
          <Input type="text" name="marca" placeholder="Marca do produto" onChange={valorInput} />

          <ButtonSuccess type="submit">Cadastrar</ButtonSuccess>

        </Form>
      </ConteudoForm>
    </Container>
  );
}
